import React from "react";
import { useCart } from "./CartContext";  // Import the CartContext
import "./Cart.css";  // Archivo CSS específico para los estilos

export default function Cart() {
  const { cartItems, removeRoomFromCart } = useCart();  // Use the cart context

  return (
    <div className="cart-container">
      <h3>Tu Reserva</h3>
      {cartItems.length === 0 ? (
        <p>No hay habitaciones en tu reserva.</p>
      ) : (
        cartItems.map((item, index) => (
          <div key={index} className="cart-item">
            <img
              src={`http://localhost:3000${item.Imagenes}`}
              alt={item.Nombre_Habitacion}
              className="cart-room-img"
            />
            <div className="cart-item-info">
              <h5>{item.Nombre_Habitacion}</h5>
              <p>{item.Descripcion}</p>
              <p><strong>Precio: </strong>${item.Precio_Base}</p>
              <p><strong>Capacidad: </strong>{item.Capacidad_Maxima} Mascotas</p>
            </div>
            <button
              className="btn btn-danger"
              onClick={() => removeRoomFromCart(item.Habitacion_ID)}
            >
              Eliminar
            </button>
          </div>
        ))
      )}
    </div>
  );
}
