import React, { useState } from "react";
import { CardElement, useElements, useStripe } from "@stripe/react-stripe-js";
import axios from "axios";

export default function CheckoutForm({ reservationId, total }) {
  const stripe = useStripe();
  const elements = useElements();
  const [error, setError] = useState(null);
  const [message, setMessage] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    const cardElement = elements.getElement(CardElement);

    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: cardElement,
    });

    if (error) {
      setError(error.message);
      return;
    }

    // Enviar el pago a tu backend
    try {
      const response = await axios.post("http://localhost:3000/api/pagos", {
        RESERVACION_ID: reservationId,
        MONTO_TOTAL: total,
        METODO_PAGO: paymentMethod.type, // Tarjeta
        ESTADO_PAGO: "pendiente", // Cambiar después del éxito
      });

      setMessage("Pago realizado con éxito.");
    } catch (error) {
      setError("Error al procesar el pago. Inténtalo de nuevo.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <CardElement />
      <button type="submit" disabled={!stripe}>
        Pagar
      </button>
      {error && <div className="error">{error}</div>}
      {message && <div className="success">{message}</div>}
    </form>
  );
}
