import React, { useEffect, useState } from "react";
import axios from "axios";
import { Modal } from "react-bootstrap"; // Utilizamos React Bootstrap para el modal
import CommonHeading from "../common/CommonHeading"; // Assuming you have a heading component

export default function Rooms() {
  const [rooms, setRooms] = useState([]);
  const [selectedRoom, setSelectedRoom] = useState(null); // Para almacenar la habitaci贸n seleccionada
  const [showModal, setShowModal] = useState(false); // Controla la visibilidad del modal

  useEffect(() => {
    // Fetch rooms data from the backend
    axios
      .get("http://localhost:3000/api/habitaciones") // Replace with your actual API route
      .then((response) => {
        setRooms(response.data);
      })
      .catch((error) => {
        console.error("Error fetching room data:", error);
      });
  }, []);

  const handleDetailsClick = (room) => {
    setSelectedRoom(room); // Guardar la habitaci贸n seleccionada
    setShowModal(true); // Mostrar el modal
  };

  const getImageUrl = (imagePath) => {
    if (imagePath && typeof imagePath === "string" && imagePath.length > 0) {
      return `http://localhost:3000${imagePath}`;
    }
    return "https://via.placeholder.com/300x200";
  };

  const getRoomClass = (estado) => {
    return estado === "Disponible" ? "room-item available" : "room-item not-available";
  };

  const handleCloseModal = () => {
    setShowModal(false); // Cerrar el modal
    setSelectedRoom(null); // Limpiar la habitaci贸n seleccionada
  };

  return (
    <div className="container-xxl py-5">
      <div className="container">
        <CommonHeading
          heading="Nuestras Habitaciones"
          title="Habitaciones"
          subtitle="Explora nuestras"
        />
        <div className="row g-4">
          {rooms.map((room, key) => (
            <div
              className="col-lg-4 col-md-6 wow fadeInUp"
              data-wow-delay="0.1s"
              key={key}
            >
              <div className={getRoomClass(room.Estado) + " shadow rounded overflow-hidden"}>
                <div className="position-relative">
                  <img
                    className="img-fluid room-img"
                    src={getImageUrl(room.Imagenes)}
                    alt={room.Nombre_Habitacion}
                  />
                  <small className="position-absolute start-0 top-100 translate-middle-y bg-primary text-white rounded py-1 px-3 ms-4">
                    ${room.Precio_Base || "N/A"}
                  </small>
                </div>
                <div className="p-4 mt-2">
                  <div className="d-flex justify-content-between mb-3">
                    <h5 className="mb-0">{room.Nombre_Habitacion}</h5>
                    <div className="ps-2">{room.Estado === "Disponible" ? "" : ""}</div>
                  </div>
                  <div className="d-flex mb-3">
                    <small className="border-end me-3 pe-3">
                      Capacidad: {room.Capacidad_Maxima} Mascotas
                    </small>
                    <small className="border-end me-3 pe-3">
                      Camas: {room.Cantidad_Camas || "N/A"}
                    </small>
                    <small>Ba帽os Privados: {room.Banos_Propios ? "S铆" : "No"}</small>
                  </div>
                  <p className="text-body mb-3">{room.Descripcion || "Descripci贸n no disponible"}</p>
                  <div className="d-flex justify-content-between">
                    <button
                      className="btn btn-sm btn-primary rounded py-2 px-4"
                      onClick={() => handleDetailsClick(room)}
                    >
                      Ver Detalles
                    </button>
                    <a className="btn btn-sm btn-dark rounded py-2 px-4" href="#">
                      Reservar Ahora
                    </a>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Modal para mostrar los detalles de la habitaci贸n */}
        {selectedRoom && (
          <Modal show={showModal} onHide={handleCloseModal} size="xl" centered>
            <Modal.Header closeButton>
              <Modal.Title>{selectedRoom.Nombre_Habitacion}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <div className="container-fluid">
                <div className="row">
                  <div className="col-md-6">
                    <img
                      className="img-fluid w-100"
                      src={getImageUrl(selectedRoom.Imagenes)}
                      alt={selectedRoom.Nombre_Habitacion}
                    />
                  </div>
                  <div className="col-md-6">
                    <h5>Detalles de la Habitaci贸n</h5>
                    <p><strong>Estado:</strong> {selectedRoom.Estado}</p>
                    <p><strong>Capacidad:</strong> {selectedRoom.Capacidad_Maxima} Mascotas</p>
                    <p><strong>Camas:</strong> {selectedRoom.Cantidad_Camas}</p>
                    <p><strong>Ba帽os Privados:</strong> {selectedRoom.Banos_Propios ? "S铆" : "No"}</p>
                    <p>{selectedRoom.Descripcion}</p>
                  </div>
                </div>
              </div>
            </Modal.Body>
            <Modal.Footer>
              <button className="btn btn-secondary" onClick={handleCloseModal}>
                Cerrar
              </button>
            </Modal.Footer>
          </Modal>
        )}
      </div>
    </div>
  );
}
