import React, { createContext, useState, useContext } from 'react';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);  // Asegúrate de inicializar como un array vacío

  // Agregar habitación y mascota al carrito
  const addRoomToCart = (room, mascota) => {
    setCartItems((prevItems) => [...prevItems, { room, mascota, services: [] }]);
  };

  // Agregar servicios seleccionados al carrito
  const addServiceToCart = (services) => {
    setCartItems((prevItems) => {
      return prevItems.map((item) => {
        if (item.room && item.mascota) {
          // Añadir los servicios seleccionados a la habitación y mascota ya agregadas
          return { ...item, services };
        }
        return item;
      });
    });
  };

  return (
    <CartContext.Provider value={{ cartItems, addRoomToCart, addServiceToCart }}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => useContext(CartContext);
