import React, { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "../AuthContext";
import "./UserForm.css";  // Archivo CSS específico para los estilos

export default function UserForm() {
  const { userData } = useAuth();  // Datos del usuario desde el contexto
  const [userFormData, setUserFormData] = useState({
    nombreUsuario: "",
    email: "",
    avatarUrl: "",
    telefono: "",
    rol: "usuario",  // Predeterminado como "usuario"
  });
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  useEffect(() => {
    if (userData) {
      setUserFormData({
        nombreUsuario: userData.NOMBRE_USUARIO,
        email: userData.CORREO,
        avatarUrl: userData.AVATAR_URL || "https://cdn-icons-png.flaticon.com/512/3135/3135768.png",  // Si hay URL de avatar, la usamos
        telefono: userData.TELEFONO || "",  // Si el teléfono existe, lo usamos
        rol: userData.ROL || "usuario",  // Si el rol existe, lo usamos
      });
    }
  }, [userData]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserFormData({
      ...userFormData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put("http://localhost:3000/api/usuarios/update", userFormData);  // No necesitas asignar el response si no lo usarás
      setSuccessMessage("Perfil actualizado correctamente.");
    } catch (error) {
      setErrorMessage("Error al actualizar el perfil. Intenta nuevamente.");
      console.error("Error al actualizar el perfil:", error);
    }
  };

  return (
    <div className="user-form__container">
      <div className="user-form__profile">
        <img
          src={userFormData.avatarUrl || "https://via.placeholder.com/150"}
          alt="User Avatar"
          className="user-form__avatar"
        />
        <p className="user-form__email">{userFormData.email}</p>
      </div>
      <div className="user-form__form-wrapper">
        <h2 className="user-form__title" center= '' >  Mi Perfil</h2>
        <form onSubmit={handleSubmit} className="user-form__form">
          <div className="user-form__form-group">
            <label htmlFor="nombreUsuario" className="user-form__label">Nombre *</label>
            <input
              type="text"
              id="nombreUsuario"
              name="nombreUsuario"
              className="user-form__input"
              value={userFormData.nombreUsuario}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="user-form__form-group">
            <label htmlFor="avatarUrl" className="user-form__label">URL Avatar *</label>
            <input
              type="url"
              id="avatarUrl"
              name="avatarUrl"
              className="user-form__input"
              value={userFormData.avatarUrl}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className="user-form__form-group">
            <label htmlFor="telefono" className="user-form__label">Teléfono</label>
            <input
              type="tel"
              id="telefono"
              name="telefono"
              className="user-form__input"
              value={userFormData.telefono}
              onChange={handleInputChange}
            />
          </div>

          <div className="user-form__form-group">
            <label htmlFor="rol" className="user-form__label">Rol</label>
            <input
              type="text"
              id="rol"
              name="rol"
              className="user-form__input"
              value={userFormData.rol}
              disabled  // El rol no debe ser editable por el usuario
            />
          </div>

          <div className="user-form__form-group">
            <label htmlFor="email" className="user-form__label">Email Address *</label>
            <input
              type="email"
              id="email"
              name="email"
              className="user-form__input"
              value={userFormData.email}
              disabled  // No permitimos cambiar el email
            />
          </div>

          <button type="submit" className="user-form__btn btn btn-primary mt-3">
            Guardar Perfil
          </button>
          {successMessage && <p className="user-form__success-message text-success">{successMessage}</p>}
          {errorMessage && <p className="user-form__error-message text-danger">{errorMessage}</p>}
        </form>
      </div>
    </div>
  );
}
