import React, { useEffect, useState } from "react";
import axios from "axios";
import CommonHeading from "../common/CommonHeading";
import { useCart } from "../../pages/CartContext";  // Importamos el contexto del carrito
import { useNavigate } from "react-router-dom";     // Para redirigir después de la selección de servicios
import "./Service.css";  // Archivo CSS específico para los estilos

export default function Services() {
  const [services, setServices] = useState([]);
  const [selectedServices, setSelectedServices] = useState([]); // Estado para los servicios seleccionados
  const { addServiceToCart, cartItems } = useCart();  // Función para agregar servicios al carrito
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch services data from the backend
    axios
      .get("http://localhost:3000/api/servicios") // Ruta de tu API que devuelve los servicios
      .then((response) => {
        const servicesData = response.data.map(service => ({
          Servicio_ID: service[0],
          Nombre_Servicio: service[1],
          Descripcion: service[2],
          Precio: service[3],
          Duracion: service[4],
          Tipo: service[5]
        }));
        setServices(servicesData);
      })
      .catch((error) => {
        console.error("Error fetching services data:", error);
      });
  }, []);

  // Función para asignar el icono basado en el tipo de servicio
  const getIconUrl = (tipo) => {
    switch (tipo) {
      case "Alimentos":
        return "https://img.icons8.com/?size=100&id=3971&format=png&color=000000";
      case "Cuidados":
        return "https://img.icons8.com/?size=100&id=2863&format=png&color=000000";
      case "Clases":
        return "https://img.icons8.com/?size=100&id=61080&format=png&color=000000";
      case "Recreacion":
        return "https://img.icons8.com/?size=100&id=56042&format=png&color=000000";
      case "Ejercitacion":
        return "https://img.icons8.com/?size=100&id=1784&format=png&color=000000";
      default:
        return "https://img.icons8.com/?size=100&id=39539&format=png&color=000000";
    }
  };

  // Agrupar servicios por tipo
  const groupServicesByType = () => {
    const grouped = services.reduce((acc, service) => {
      if (!acc[service.Tipo]) {
        acc[service.Tipo] = [];
      }
      acc[service.Tipo].push(service);
      return acc;
    }, {});
    return grouped;
  };

  const groupedServices = groupServicesByType();

  // Función para manejar la selección de un servicio (selección múltiple)
  const handleServiceClick = (service) => {
    setSelectedServices((prevSelected) => {
      if (prevSelected.includes(service.Servicio_ID)) {
        return prevSelected.filter((id) => id !== service.Servicio_ID);
      } else {
        return [...prevSelected, service.Servicio_ID];
      }
    });
  };

  const getServiceClass = (tipo, isSelected) => {
    let baseClass = tipo === "Premium" ? "service-item premium" : "service-item";
    return isSelected ? `${baseClass} selected` : baseClass;
  };

  // Función para añadir los servicios seleccionados al carrito
  const handleAddToCart = () => {
    const selectedServiceObjects = services.filter(service => selectedServices.includes(service.Servicio_ID));
    addServiceToCart(selectedServiceObjects);  // Agregar servicios al carrito
    navigate("/carrito");  // Redirigir al carrito para ver los servicios agregados
  };

  return (
    <div className="container-xxl py-5">
      <div className="container">
        <div className="text-center wow fadeInUp" data-wow-delay="0.1s">
          <CommonHeading
            heading="Nuestros Servicios"
            title="Servicios"
            subtitle="Explora nuestros"
          />
        </div>
        {Object.keys(groupedServices).map((tipo, index) => (
          <div key={index} className="service-group">
            <h3 className="text-center my-4">{tipo}</h3>
            <div className="row g-4">
              {groupedServices[tipo].map((service, idx) => (
                <div
                  className="col-lg-4 col-md-6 wow fadeInUp"
                  data-wow-delay="0.1s"
                  key={idx}
                >
                  <div
                    className={getServiceClass(service.Tipo, selectedServices.includes(service.Servicio_ID))}
                    onClick={() => handleServiceClick(service)}
                  >
                    <div className="service-icon p-1">
                      <div className="w-100 h-100 d-flex align-items-center justify-content-center">
                        <img
                          src={getIconUrl(service.Tipo)}
                          alt={service.Nombre_Servicio}
                          className="service-icon-img"
                        />
                      </div>
                    </div>
                    <h5 className="mb-3">{service.Nombre_Servicio}</h5>
                    <p className="text-body mb-0">
                      {service.Descripcion || "Descripción no disponible"}
                    </p>
                    <p className="service-price">Precio: ${service.Precio}</p>
                    <p className="service-duration">
                      Duración: {service.Duracion} minutos
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}

        {/* Botón para agregar los servicios seleccionados al carrito */}
        <div className="text-center mt-4">
          <Button variant="primary" onClick={handleAddToCart} disabled={selectedServices.length === 0}>
            Agregar Servicios Seleccionados al Carrito
          </Button>
        </div>
      </div>
    </div>
  );
}
