import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import CommonHeading from "../common/CommonHeading"; // Assuming you have a heading component

export default function Rooms() {
  const [rooms, setRooms] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch rooms data from the backend
    axios
      .get("http://localhost:3000/api/habitaciones") // Replace with your actual API route
      .then((response) => {
        setRooms(response.data);
      })
      .catch((error) => {
        console.error("Error fetching room data:", error);
      });
  }, []);

  const handleDetailsClick = (id) => {
    navigate(`/room/${id}`);
  };

  return (
    <div className="container-xxl py-5">
      <div className="container">
        <CommonHeading
          heading="Nuestras Habitaciones"
          title="Habitaciones"
          subtitle="Explora nuestras"
        />
        <div className="row g-4">
          {rooms.map((room, key) => (
            <div className="col-lg-4 col-md-6 wow fadeInUp" data-wow-delay="0.1s" key={key}>
              <div className="room-item shadow rounded overflow-hidden">
                <div className="position-relative">
                  {/* Manejar el campo IMAGENES: si es null, mostrar placeholder */}
                  <img
                    className="img-fluid"
                    src={room[10] ? room[10].split(",")[0] : "https://via.placeholder.com/150"}
                    alt="Room"
                  />
                  <small className="position-absolute start-0 top-100 translate-middle-y bg-primary text-white rounded py-1 px-3 ms-4">
                    ${room[7] || 'N/A'} {/* Mostrar 'N/A' si no hay precio */}
                  </small>
                </div>
                <div className="p-4 mt-2">
                  <div className="d-flex justify-content-between mb-3">
                    <h5 className="mb-0">{room[1]}</h5>
                    <div className="ps-2">{room[2] === 'Disponible' ? 'ðŸŸ¢' : 'ðŸ”´'}</div>
                  </div>
                  <div className="d-flex mb-3">
                    <small className="border-end me-3 pe-3">
                      Capacidad: {room[3]} 
                    </small>
                    <small className="border-end me-3 pe-3">
                      Camas: {room[9] || 'N/A'}
                    </small>
                    <small>
                      BaÃ±os Privados: {room[8] ? 'SÃ­' : 'No'}
                    </small>
                  </div>
                  <p className="text-body mb-3">{room[6] || 'DescripciÃ³n no disponible'}</p>
                  <div className="d-flex justify-content-between">
                    <button
                      className="btn btn-sm btn-primary rounded py-2 px-4"
                      onClick={() => handleDetailsClick(room[0])}
                    >
                      Ver Detalles
                    </button>
                    <a className="btn btn-sm btn-dark rounded py-2 px-4" href="#">
                      Reservar Ahora
                    </a>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
