import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";
import { useAuth } from "../AuthContext";

export default function UserForm() {
  const { userData } = useAuth();  // Extraemos los datos del usuario desde el contexto (en caso de que sea edición)
  const [userFormData, setUserFormData] = useState({
    nombreUsuario: "",
    correo: "",
    contraseña: "",
    telefono: "",
    rol: "usuario",  // Valor por defecto
  });
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();
  const { id } = useParams();  // En caso de que estemos editando un usuario ya existente

  useEffect(() => {
    if (id && userData) {
      // Si hay un ID de usuario en la URL y ya tenemos datos del usuario, llenamos el formulario con los datos
      setUserFormData({
        nombreUsuario: userData.NOMBRE_USUARIO,
        correo: userData.CORREO,
        contraseña: "",  // No prellenamos la contraseña por seguridad
        telefono: userData.TELEFONO || "",
        rol: userData.ROL || "usuario",
      });
    }
  }, [id, userData]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserFormData({
      ...userFormData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (id) {
      // Si existe un ID, estamos en modo de edición
      try {
        await axios.put(`http://localhost:3000/api/usuarios/${id}`, userFormData);
        navigate("/perfil");  // Navegamos a la página de perfil o donde prefieras
      } catch (error) {
        setErrorMessage("Error al actualizar el usuario. Intenta de nuevo.");
        console.error("Error al actualizar el usuario:", error);
      }
    } else {
      // Si no hay ID, estamos en modo de creación de nuevo usuario
      try {
        await axios.post("http://localhost:3000/api/usuarios", userFormData);
        navigate("/perfil");
      } catch (error) {
        setErrorMessage("Error al registrar el usuario. Intenta de nuevo.");
        console.error("Error al registrar el usuario:", error);
      }
    }
  };

  return (
    <div className="container">
      <h2>{id ? "Editar Usuario" : "Registrar Nuevo Usuario"}</h2>
      {errorMessage && <p className="text-danger">{errorMessage}</p>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="nombreUsuario">Nombre de Usuario</label>
          <input
            type="text"
            id="nombreUsuario"
            name="nombreUsuario"
            className="form-control"
            value={userFormData.nombreUsuario}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="correo">Correo</label>
          <input
            type="email"
            id="correo"
            name="correo"
            className="form-control"
            value={userFormData.correo}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="contraseña">Contraseña</label>
          <input
            type="password"
            id="contraseña"
            name="contraseña"
            className="form-control"
            value={userFormData.contraseña}
            onChange={handleInputChange}
            required={!id}  // Hacemos que la contraseña sea obligatoria solo si es un nuevo registro
          />
        </div>

        <div className="form-group">
          <label htmlFor="telefono">Teléfono</label>
          <input
            type="text"
            id="telefono"
            name="telefono"
            className="form-control"
            value={userFormData.telefono}
            onChange={handleInputChange}
          />
        </div>

        <div className="form-group">
          <label htmlFor="rol">Rol</label>
          <select
            id="rol"
            name="rol"
            className="form-control"
            value={userFormData.rol}
            onChange={handleInputChange}
          >
            <option value="usuario">Usuario</option>
            <option value="admin">Admin</option>
          </select>
        </div>

        <button type="submit" className="btn btn-primary mt-3">
          {id ? "Guardar Cambios" : "Registrar"}
        </button>
      </form>
    </div>
  );
}
