import React from 'react';
import { useCart } from './CartContext';  // Asegúrate de que el contexto está importado correctamente
import { useNavigate } from "react-router-dom";  // Para redirigir a la página de servicios

// Función para obtener la URL de la imagen de la habitación correctamente
const getImageUrl = (imagePath) => {
  if (imagePath && typeof imagePath === "string" && imagePath.length > 0) {
    return `http://localhost:3000${imagePath}`;
  }
  return "https://via.placeholder.com/300x200";
};

// Función para obtener la URL de la imagen de la mascota correctamente
const getPetImageUrl = (imagePath) => {
  const avatarUrl = imagePath || "https://via.placeholder.com/150";
  return avatarUrl.startsWith('http') ? avatarUrl : `http://localhost:3000${avatarUrl}`;
};

const Cart = () => {
  const { cartItems } = useCart();  // Asumiendo que el contexto devuelve 'cartItems'
  const navigate = useNavigate();

  // Verificamos si 'cartItems' está definido y es un array
  if (!cartItems || cartItems.length === 0) {
    return <div className="cart cart-empty">No hay habitaciones seleccionadas.</div>;
  }

  return (
    <div className="cart">
      <h3 className="cart-title">Tu Carrito</h3>
      {cartItems.map((item, index) => {
        return (
          <div key={index} className="cart-item">
            {/* Datos de la habitación */}
            {item.room && (
              <>
                <h4>Datos de la Habitación</h4>
                <img 
                  src={getImageUrl(item.room.Imagenes)} 
                  alt={item.room.Nombre_Habitacion} 
                  className="cart-room-img" 
                />
                <div className="cart-item-info">
                  <p><strong>ID Habitación:</strong> {item.room.Habitacion_ID}</p>
                  <p><strong>Nombre Habitación:</strong> {item.room.Nombre_Habitacion}</p>
                  <p><strong>Precio:</strong> ${item.room.Precio_Base}</p>
                  <p><strong>Capacidad:</strong> {item.room.Capacidad_Maxima} mascotas</p>
                  <p><strong>Camas:</strong> {item.room.Cantidad_Camas || "N/A"}</p>
                  <p><strong>Baños Privados:</strong> {item.room.Banos_Propios ? "Sí" : "No"}</p>
                  <p><strong>Descripción:</strong> {item.room.Descripcion}</p>
                  <p><strong>Tipo de Habitación:</strong> {item.room.Tipo_Habitacion}</p>
                  <p><strong>Ubicación:</strong> {item.room.Ubicacion}</p>
                </div>
              </>
            )}

            {/* Datos de la mascota */}
            {item.mascota && (
              <>
                <h4>Datos de la Mascota</h4>
                <img 
                  src={getPetImageUrl(item.mascota[10])} 
                  alt={item.mascota[2]} 
                  className="cart-pet-img" 
                />
                <div className="cart-pet-info">
                  <p><strong>ID Mascota:</strong> {item.mascota[0]}</p>
                  <p><strong>Nombre Mascota:</strong> {item.mascota[2]}</p>
                  <p><strong>Especie:</strong> {item.mascota[3]}</p>
                  <p><strong>Edad:</strong> {item.mascota[7]} años</p>
                  <p><strong>Peso:</strong> {item.mascota[5]} kg</p>
                  <p><strong>Categoría:</strong> {item.mascota[9]}</p>
                </div>
              </>
            )}

            {/* Mostrar los servicios seleccionados si existen */}
            {item.services && item.services.length > 0 && (
              <div>
                <h4>Servicios Seleccionados</h4>
                <ul>
                  {item.services.map((service, i) => (
                    <li key={i}>
                      <p><strong>{service.Nombre_Servicio}</strong> - ${service.Precio}</p>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default Cart;
