import React, { useEffect, useState } from "react";
import axios from "axios";
import { Modal, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom"; // Para redirigir al registro de mascotas
import CommonHeading from "../common/CommonHeading";
import { useCart } from "../../pages/CartContext"; // Importamos el contexto del carrito
import { useAuth } from "../../AuthContext"; // Importamos el contexto de autenticaci贸n
import Cart from "../../pages/Cart"; // Importamos el componente del carrito
import "./Rooms.css";

export default function Rooms() {
  const [rooms, setRooms] = useState([]);
  const [selectedRoom, setSelectedRoom] = useState(null);
  const [selectedMascota, setSelectedMascota] = useState(null); // Para la mascota seleccionada
  const [mascotas, setMascotas] = useState([]); // Mascotas del usuario
  const [showModal, setShowModal] = useState(false);
  const { userData } = useAuth(); // Datos del usuario autenticado
  const { cartItems, addRoomToCart } = useCart(); // Incluimos cartItems para verificar si hay algo en el carrito
  const navigate = useNavigate(); // Hook para navegar entre p谩ginas

  useEffect(() => {
    // Obtener las habitaciones disponibles
    axios
      .get("http://localhost:3000/api/habitaciones")
      .then((response) => {
        setRooms(response.data);
      })
      .catch((error) => {
        console.error("Error fetching room data:", error);
      });

    // Obtener las mascotas del usuario autenticado
    if (userData && userData.USUARIO_ID) {
      axios.get(`http://localhost:3000/api/mascotas/${userData.USUARIO_ID}`)
        .then((response) => {
          console.log('Mascotas obtenidas:', response.data);
          setMascotas(response.data);
        })
        .catch((error) => {
          console.error("Error fetching mascotas data:", error);
        });
    }
  }, [userData]);

  const handleDetailsClick = (room) => {
    setSelectedRoom(room);
    setShowModal(true);
  };

  const handleReserveClick = (room) => {
    if (selectedMascota) {
      // A帽adimos la habitaci贸n seleccionada al carrito junto con la mascota
      addRoomToCart({ ...room, mascota: selectedMascota });
    } else {
      alert("Por favor, selecciona una mascota antes de reservar.");
    }
  };

  const handleSelectMascota = (mascota) => {
    setSelectedMascota(mascota);
  };

  // Funci贸n para redirigir a la p谩gina de registro de mascotas
  const handleRedirectToMascotaForm = () => {
    navigate("/mascota-form"); // Redirige a la ruta del formulario de mascotas
  };

  // Obtener la URL correcta de la imagen de la mascota
  const getImageUrl = (imagePath) => {
    if (imagePath && typeof imagePath === "string" && imagePath.length > 0) {
      // Asegurarse de que la URL se maneja correctamente
      return imagePath.startsWith('http')
        ? imagePath
        : `http://localhost:3000${imagePath}`;
    }
    return "https://via.placeholder.com/150"; // Fallback si no hay imagen
  };

  const getRoomClass = (estado) => {
    return estado === "Disponible" ? "room-item available" : "room-item not-available";
  };

  return (
    <div className="container-xxl py-5">
      <div className="container-fluid">
        <div className="row">
          {/* Secci贸n para seleccionar mascotas */}
          <div className="mascotas__horizontal-container">
            {mascotas.length > 0 ? (
              mascotas.map((mascota) => (
                <div
                  className={`mascota__card-horizontal ${selectedMascota?.[0] === mascota[0] ? 'selected' : ''}`}
                  key={mascota[0]}
                  onClick={() => handleSelectMascota(mascota)}
                >
                  <img
                    src={getImageUrl(mascota[10])} // Obtener correctamente la imagen de la mascota
                    alt={mascota[2]}
                    className="mascota__card-img-horizontal"
                  />
                  <div className="mascota__card-info-horizontal">
                    <h5>{mascota[2]}</h5>
                    <p>Especie: {mascota[3]}</p>
                    <p>Edad: {mascota[7]} a帽os</p>
                  </div>
                </div>
              ))
            ) : (
              <div className="no-mascotas">
                <p>No tienes mascotas registradas.</p>
                <button className="btn btn-primary" onClick={handleRedirectToMascotaForm}>
                  Registrar Mascota
                </button>
              </div>
            )}
          </div>

          {/* Main Rooms Section */}
          <div className="col-lg-9">
            <CommonHeading
              heading="Nuestras Habitaciones"
              title="Habitaciones"
              subtitle="Explora nuestras"
            />
            <div className="row g-4">
              {rooms.map((room, key) => (
                <div className="col-lg-4 col-md-6 wow fadeInUp" data-wow-delay="0.1s" key={key}>
                  <div className={getRoomClass(room.Estado) + " shadow rounded overflow-hidden"}>
                    <div className="position-relative">
                      <img
                        className="img-fluid room-img"
                        src={getImageUrl(room.Imagenes)}
                        alt={room.Nombre_Habitacion}
                      />
                      <small className="position-absolute start-0 top-100 translate-middle-y bg-primary text-white rounded py-1 px-3 ms-4">
                        ${room.Precio_Base || "N/A"}
                      </small>
                    </div>
                    <div className="p-4 mt-2">
                      <div className="d-flex justify-content-between mb-3">
                        <h5 className="mb-0">{room.Nombre_Habitacion}</h5>
                        <div className="ps-2">{room.Estado === "Disponible" ? "" : ""}</div>
                      </div>
                      <div className="d-flex mb-3">
                        <small className="border-end me-3 pe-3">
                          Capacidad: {room.Capacidad_Maxima} Mascotas
                        </small>
                        <small className="border-end me-3 pe-3">
                          Camas: {room.Cantidad_Camas || "N/A"}
                        </small>
                        <small>Ba帽os Privados: {room.Banos_Propios ? "S铆" : "No"}</small>
                      </div>
                      <p className="text-body mb-3">{room.Descripcion || "Descripci贸n no disponible"}</p>
                      <div className="d-flex justify-content-between">
                        <button
                          className="btn btn-sm btn-primary rounded py-2 px-4"
                          onClick={() => handleDetailsClick(room)}
                        >
                          Ver Detalles
                        </button>
                        <button
                          className="btn btn-sm btn-dark rounded py-2 px-4"
                          onClick={() => handleReserveClick(room)}
                        >
                          Reservar Ahora
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {selectedRoom && (
              <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
                <Modal.Header closeButton>
                  <Modal.Title>{selectedRoom.Nombre_Habitacion}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                  <img
                    className="img-fluid mb-4"
                    src={getImageUrl(selectedRoom.Imagenes)}
                    alt={selectedRoom.Nombre_Habitacion}
                  />
                  <h5>Descripci贸n</h5>
                  <p>{selectedRoom.Descripcion}</p>
                  <h5>Detalles</h5>
                  <ul>
                    <li>Estado: {selectedRoom.Estado}</li>
                    <li>Capacidad: {selectedRoom.Capacidad_Maxima} mascotas</li>
                    <li>Tipo de habitaci贸n: {selectedRoom.Tipo_Habitacion}</li>
