import React from "react";
import "./css/style.css";
import "./css/bootstrap.min.css";
import "./css/animate.css";
import "./css/animate.min.css";
import "./App.css";
import Header from "./components/common/Header";
import Footer from "./components/common/Footer";
import { Route, BrowserRouter as Router, Routes } from "react-router-dom";
import { AuthProvider } from "./AuthContext";  // Importa el proveedor de autenticación
import ProtectedRoute from "./ProtectedRoute";  // Importa el componente de rutas protegidas

// Importa las páginas
import {
  Home,
  Booking,
  AboutUs,
  Contact,
  PageNotFound,
  Room,
  Services,
  Team,
  Testimonial,
  ReservasActivas,
} from "./pages/index";
import RoomDetails from "./components/RoomDetails";  // Nueva importación del componente de detalles
import Login from "./components/Login";  // Asegúrate de importar el componente Login

export default function App() {
  return (
    <Router>  {/* Router debe envolver todo */}
      <AuthProvider>  {/* AuthProvider debe estar dentro del Router */}
        <div className="app-container">
          <Header />
          <div className="content-container">
            <Routes>
              {/* Rutas abiertas */}
              <Route path="/login" element={<Login />} />
              <Route path="/about" element={<AboutUs />} />
              <Route path="/contact" element={<Contact />} />

              {/* Rutas protegidas */}
              <Route
                path="/"
                element={
                  <ProtectedRoute>
                    <Home />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/booking"
                element={
                  <ProtectedRoute>
                    <Booking />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/reservas-activas"
                element={
                  <ProtectedRoute>
                    <ReservasActivas />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/team"
                element={
                  <ProtectedRoute>
                    <Team />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/rooms"
                element={
                  <ProtectedRoute>
                    <Room />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/services"
                element={
                  <ProtectedRoute>
                    <Services />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/room/:id"
                element={
                  <ProtectedRoute>
                    <RoomDetails />
                  </ProtectedRoute>
                }
              />

              {/* Ruta de página no encontrada */}
              <Route path="/*" element={<PageNotFound />} />
            </Routes>
          </div>
          <Footer />
        </div>
      </AuthProvider>
    </Router>
  );
}
