import React, { useState } from "react";
import { Link } from "react-router-dom";
import { navList } from "../data/Data";
import SocialIcons from "./SocialIcons";
import { useAuth } from "../../AuthContext";  // Importa el contexto de autenticaciÃ³n

export default function Header() {
  const [navbarCollapse, setNavbarCollapse] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [showUserDropdown, setShowUserDropdown] = useState(false);
  const { userData } = useAuth();  // Extrae los datos del usuario desde el contexto

  const handleMouseEnter = (itemId) => {
    setActiveDropdown(itemId);
  };

  const handleMouseLeave = () => {
    setActiveDropdown(null);
  };

  const handleUserMouseEnter = () => {
    setShowUserDropdown(true);
  };

  const handleUserMouseLeave = () => {
    setShowUserDropdown(false);
  };

  return (
    <>
      <div className="container-fluid bg-dark px-0">
        <div className="row gx-0">
          <div className="col-lg-3 bg-dark d-none d-lg-block">
            <Link
              to="/"
              className="navbar-brand w-100 h-100 m-0 p-0 d-flex align-items-center justify-content-center"
            >
              <h1 className="m-0 text-primary text-uppercase">PETHEAVEN</h1>
            </Link>
          </div>
          <div className="col-lg-9">
            <nav className="navbar navbar-expand-lg bg-dark navbar-dark p-3 p-lg-0">
              <Link to="/" className="navbar-brand d-block d-lg-none">
                <h1 className="m-0 text-primary text-uppercase">Petheaven</h1>
              </Link>
              <button
                type="button"
                className="navbar-toggler"
                onClick={() => setNavbarCollapse(!navbarCollapse)}
              >
                <span className="navbar-toggler-icon"></span>
              </button>
              <div
                className={
                  navbarCollapse
                    ? "navbar-collapse justify-content-around navbarCollapse"
                    : "collapse navbar-collapse justify-content-around"
                }
              >
                <div className="navbar-nav mr-auto py-0">
                  {navList.map((item, index) => (
                    <div key={index}>
                      {item.subItems ? (
                        <div
                          className="nav-item dropdown"
                          onMouseEnter={() => handleMouseEnter(item.id)}
                          onMouseLeave={handleMouseLeave}
                        >
                          <Link className="nav-link dropdown-toggle">
                            {item.text}
                          </Link>
                          <div
                            className={`dropdown-menu rounded-0 m-0 ${
                              activeDropdown === item.id ? "show" : ""
                            }`}
                          >
                            {item.subItems.map((sub) => (
                              <Link to={sub.path} className="dropdown-item" key={sub.id}>
                                {sub.text}
                              </Link>
                            ))}
                          </div>
                        </div>
                      ) : (
                        <Link to={item.path} className="nav-item nav-link">
                          {item.text}
                        </Link>
                      )}
                    </div>
                  ))}
                </div>

                {/* SecciÃ³n de usuario */}
                <div
                  className="nav-item dropdown"
                  onMouseEnter={handleUserMouseEnter}
                  onMouseLeave={handleUserMouseLeave}
                >
                  <img
                    src=""
                    alt="User"
                    className="rounded-circle"
                    style={{ width: "40px", height: "40px", cursor: "pointer", marginRight: "10px" }}
                  />
                  {/* Mostrar el nombre del usuario si estÃ¡ autenticado */}
                  <span className="text-white">{userData?.NOMBRE_USUARIO || "Usuario"}</span>
                  <div
                    className={`dropdown-menu dropdown-menu-end ${
                      showUserDropdown ? "show" : ""
                    }`}
                    style={{
                      backgroundColor: "#f8f9fa",
                      border: "1px solid #ddd",
                      padding: "10px",
                      borderRadius: "5px"
                    }}
                  >
                    <Link to="/ReservasActivas" className="dropdown-item">
                      ConfiguraciÃ³n de perfil
                    </Link>
                    <Link to="/edit-profile" className="dropdown-item">
                      Editar perfil
                    </Link>
                    <Link to="/logout" className="dropdown-item">
                      Cerrar sesiÃ³n
                    </Link>
                  </div>
                </div>

                <SocialIcons />
              </div>
            </nav>
          </div>
        </div>
      </div>
    </>
  );
}
