import React, { useState, useEffect } from "react";
import Heading from "../components/common/Heading";
import { useCart } from "../pages/CartContext"; // Para acceder al carrito
import axios from "axios";
import "./Booking.css";

export default function Booking() {
  const { cartItems } = useCart(); // Obtenemos los elementos del carrito
  const [formData, setFormData] = useState({
    mascotaId: cartItems.mascota ? cartItems.mascota[0] : "", // Prellenar el ID de la mascota
    habitacionId: cartItems.room ? cartItems.room.Habitacion_ID : "", // Prellenar el ID de la habitación
    fechaEntrada: "",
    fechaSalida: "",
    estadoReservacion: "Pendiente", // Valor por defecto
    total: "", // Se calculará automáticamente
    notas: "",
  });

  const [message, setMessage] = useState(""); // Estado para manejar mensajes

  // Calcula el total sumando el precio de la habitación y los servicios
  useEffect(() => {
    if (cartItems.room && cartItems.services.length > 0) {
      const totalServices = cartItems.services.reduce((total, service) => total + service.Precio, 0);
      const totalPrice = cartItems.room.Precio_Base + totalServices;
      setFormData((prevFormData) => ({
        ...prevFormData,
        total: totalPrice.toFixed(2), // Formato de dos decimales
      }));
    }
  }, [cartItems]);

  // Función para manejar el cambio en los campos del formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Función para manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:3000/api/reservaciones", {
        Mascota_ID: formData.mascotaId,
        Habitacion_ID: formData.habitacionId,
        Fecha_Entrada: formData.fechaEntrada,
        Fecha_Salida: formData.fechaSalida,
        Estado_Reservacion: formData.estadoReservacion,
        Total: formData.total,
        Notas: formData.notas,
      });

      setMessage("Reserva realizada con éxito.");
      setFormData({
        mascotaId: "",
        habitacionId: "",
        fechaEntrada: "",
        fechaSalida: "",
        estadoReservacion: "Pendiente",
        total: "",
        notas: "",
      });
    } catch (error) {
      setMessage("Ocurrió un error al realizar la reserva. Inténtalo de nuevo.");
      console.error("Error al realizar la reserva:", error);
    }
  };

  return (
    <>
      <Heading heading="Reservas" title="Home" subtitle="Reservas" />
      <div className="booking-container">
        <div className="reservation-summary">
          <h3>Detalles de la Reserva</h3>

          {/* Mostrar información de la habitación */}
          {cartItems.room && (
            <div className="room-info">
              <h4>Habitación Seleccionada</h4>
              <p><strong>Nombre:</strong> {cartItems.room.Nombre_Habitacion}</p>
              <p><strong>Capacidad:</strong> {cartItems.room.Capacidad_Maxima}</p>
              <p><strong>Precio:</strong> ${cartItems.room.Precio_Base}</p>
            </div>
          )}

          {/* Mostrar información de la mascota */}
          {cartItems.mascota && (
            <div className="pet-info">
              <h4>Mascota</h4>
              <p><strong>Nombre:</strong> {cartItems.mascota[2]}</p>
              <p><strong>Especie:</strong> {cartItems.mascota[3]}</p>
              <p><strong>Edad:</strong> {cartItems.mascota[7]} años</p>
            </div>
          )}

          {/* Mostrar servicios seleccionados */}
          {cartItems.services.length > 0 && (
            <div className="services-info">
              <h4>Servicios Seleccionados</h4>
              {cartItems.services.map((service, index) => (
                <div key={index}>
                  <p><strong>{service.Nombre_Servicio}</strong>: ${service.Precio}</p>
                </div>
              ))}
            </div>
          )}

          {/* Mostrar el total a pagar */}
          <div className="total-info">
            <h4>Total a Pagar: ${formData.total}</h4>
          </div>
        </div>

        {/* Formulario para confirmar y agendar la reserva */}
        <form className="booking-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="fechaEntrada">Fecha de Entrada:</label>
            <input
              type="datetime-local"
              id="fechaEntrada"
              name="fechaEntrada"
              value={formData.fechaEntrada}
              onChange={handleChange}
              required
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label htmlFor="fechaSalida">Fecha de Salida:</label>
            <input
              type="datetime-local"
              id="fechaSalida"
              name="fechaSalida"
              value={formData.fechaSalida}
              onChange={handleChange}
              required
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label htmlFor="notas">Notas Adicionales:</label>
            <textarea
              id="notas"
              name="notas"
              value={formData.notas}
              onChange={handleChange}
              placeholder="Escribe alguna instrucción especial"
              className="form-control"
            ></textarea>
          </div>
          <button type="submit" className="btn btn-primary">
            Confirmar Reserva
          </button>
        </form>

        {/* Mostramos el mensaje de éxito o error */}
        {message && <p className="message">{message}</p>}
      </div>
    </>
  );
}
