import React, { useEffect, useState } from "react";
import axios from "axios";
import CommonHeading from "../common/CommonHeading";

export default function Services() {
  const [services, setServices] = useState([]);

  useEffect(() => {
    // Fetch services data from the backend
    axios
      .get("http://localhost:3000/api/servicios") // Ruta de tu API que devuelve los servicios
      .then((response) => {
        setServices(response.data);
      })
      .catch((error) => {
        console.error("Error fetching services data:", error);
      });
  }, []);

  // Agrupar servicios por tipo
  const groupServicesByType = () => {
    const grouped = services.reduce((acc, service) => {
      if (!acc[service.Tipo]) {
        acc[service.Tipo] = [];
      }
      acc[service.Tipo].push(service);
      return acc;
    }, {});
    return grouped;
  };

  const groupedServices = groupServicesByType();

  const getServiceClass = (tipo) => {
    return tipo === "Premium" ? "service-item premium" : "service-item";
  };

  return (
    <div className="container-xxl py-5">
      <div className="container">
        <div className="text-center wow fadeInUp" data-wow-delay="0.1s">
          <CommonHeading
            heading="Nuestros Servicios"
            title="Servicios"
            subtitle="Explora nuestros"
          />
        </div>
        {Object.keys(groupedServices).map((tipo, index) => (
          <div key={index} className="service-group">
            <h3 className="text-center my-4">{tipo}</h3>
            <div className="row g-4">
              {groupedServices[tipo].map((service, idx) => (
                <div className="col-lg-4 col-md-6 wow fadeInUp" data-wow-delay="0.1s" key={idx}>
                  <div className={getServiceClass(service.Tipo) + " shadow rounded overflow-hidden"}>
                    <div className="service-icon bg-transparent border rounded p-1">
                      <div className="w-100 h-100 border rounded d-flex align-items-center justify-content-center">
                        <img src={service.iconUrl || "https://via.placeholder.com/50"} alt={service.Nombre_Servicio} className="service-icon-img" />
                      </div>
                    </div>
                    <h5 className="mb-3">{service.Nombre_Servicio}</h5>
                    <p className="text-body mb-0">{service.Descripcion || "Descripción no disponible"}</p>
                    <p className="service-price">Precio: ${service.Precio}</p>
                    <p className="service-duration">Duración: {service.Duracion} minutos</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
