import React from 'react';
import { useCart } from '.';  // Asegúrate de que el contexto está importado correctamente

const Cart = () => {
  const { cartItems } = useCart();  // Asumiendo que el contexto devuelve 'cartItems'

  // Verificamos si 'cartItems' está definido y es un array
  if (!cartItems || cartItems.length === 0) {
    return <div>No hay habitaciones seleccionadas.</div>;
  }

  return (
    <div className="cart">
      <h3>Tu Carrito</h3>
      {cartItems.map((item, index) => (
        <div key={index} className="cart-item">
          <h5>{item.Nombre_Habitacion}</h5>
          <p>Precio: ${item.Precio_Base}</p>
          <img src={item.Imagenes} alt={item.Nombre_Habitacion} style={{ width: '100px', height: '100px' }} />
        </div>
      ))}
    </div>
  );
};

export default Cart;
