import React, { createContext, useState, useContext } from 'react';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState({
    room: null,
    mascota: null,
    services: [],
    checkIn: "", // Agregar fecha de entrada
    checkOut: "", // Agregar fecha de salida
  });

  // Función para agregar la habitación, mascota y las fechas al carrito
  const addRoomToCart = (room, mascota, checkIn, checkOut) => {
    setCartItems((prevItems) => ({
      ...prevItems,
      room,
      mascota,
      checkIn,   // Guardar la fecha de entrada
      checkOut,  // Guardar la fecha de salida
    }));
  };

  // Función para agregar un servicio al carrito
  const addServiceToCart = (service) => {
    setCartItems((prevItems) => ({
      ...prevItems,
      services: [...prevItems.services, service]
    }));
  };

  // Función para eliminar un servicio del carrito
  const removeServiceFromCart = (serviceID) => {
    setCartItems((prevItems) => ({
      ...prevItems,
      services: prevItems.services.filter(service => service.Servicio_ID !== serviceID)
    }));
  };

  return (
    <CartContext.Provider value={{ cartItems, addRoomToCart, addServiceToCart, removeServiceFromCart }}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => useContext(CartContext);
