import React, { createContext, useState } from 'react';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [selectedRooms, setSelectedRooms] = useState([]);

  const addRoomToCart = (room) => {
    setSelectedRooms((prevRooms) => [...prevRooms, room]);
  };

  const removeRoomFromCart = (roomId) => {
    setSelectedRooms((prevRooms) => prevRooms.filter(room => room.Habitacion_ID !== roomId));
  };

  const clearCart = () => {
    setSelectedRooms([]);
  };

  return (
    <CartContext.Provider value={{ selectedRooms, addRoomToCart, removeRoomFromCart, clearCart }}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => React.useContext(CartContext);
