import React from "react";
import "./css/style.css";
import "./css/bootstrap.min.css";
import "./css/animate.css";
import "./css/animate.min.css";
import "./App.css";
import Header from "./components/common/Header";
import Footer from "./components/common/Footer";
import { Route, BrowserRouter as Router, Routes } from "react-router-dom";
import { AuthProvider } from "./AuthContext";  // Proveedor de autenticación
import ProtectedRoute from "./ProtectedRoute";  // Componente para proteger las rutas

// Páginas principales
import {
  Home,
  Booking,
  AboutUs,
  Contact,
  PageNotFound,
  Room,
  Services,
  Team,
  Testimonial,
  ReservasActivas,
} from "./pages/index";
import RoomDetails from "./components/RoomDetails";  // Detalles de habitación
import Login from "./components/Login";  // Componente Login

export default function App() {
  return (
    <Router>
      <AuthProvider>
        {/* Verifica si el usuario está autenticado */}
        <Routes>
          {/* Ruta para el login que no necesita protección */}
          <Route path="/login" element={<Login />} />

          {/* Las demás rutas estarán protegidas */}
          <Route
            path="/"
            element={
              <ProtectedRoute>
                <Home />
              </ProtectedRoute>
            }
          />
          <Route
            path="/booking"
            element={
              <ProtectedRoute>
                <Booking />
              </ProtectedRoute>
            }
          />
          <Route
            path="/reservas-activas"
            element={
              <ProtectedRoute>
                <ReservasActivas />
              </ProtectedRoute>
            }
          />
          <Route
            path="/team"
            element={
              <ProtectedRoute>
                <Team />
              </ProtectedRoute>
            }
          />
          <Route
            path="/rooms"
            element={
              <ProtectedRoute>
                <Room />
              </ProtectedRoute>
            }
          />
          <Route
            path="/services"
            element={
              <ProtectedRoute>
                <Services />
              </ProtectedRoute>
            }
          />
          <Route
            path="/room/:id"
            element={
              <ProtectedRoute>
                <RoomDetails />
              </ProtectedRoute>
            }
          />

          {/* Rutas públicas (si las necesitas) */}
          <Route path="/about" element={<AboutUs />} />
          <Route path="/contact" element={<Contact />} />

          {/* Ruta de página no encontrada */}
          <Route path="/*" element={<PageNotFound />} />
        </Routes>
      </AuthProvider>
    </Router>
  );
}
