import React, { createContext, useContext, useState } from "react";

// Create the CartContext
const CartContext = createContext();

// Custom hook to use the cart
export const useCart = () => useContext(CartContext);

// Cart provider component
export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);  // Store items in the cart

  // Function to add a room to the cart
  const addRoomToCart = (room) => {
    setCartItems((prevItems) => [...prevItems, room]);
  };

  // Function to remove a room from the cart
  const removeRoomFromCart = (roomId) => {
    setCartItems((prevItems) => prevItems.filter((item) => item.Habitacion_ID !== roomId));
  };

  return (
    <CartContext.Provider value={{ cartItems, addRoomToCart, removeRoomFromCart }}>
      {children}
    </CartContext.Provider>
  );
};
