import React from "react";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";

export default function CheckoutForm({ reservationId, total }) {
  const stripe = useStripe();
  const elements = useElements();

  const handlePaymentSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      return; // Stripe.js aún no se ha cargado
    }

    const cardElement = elements.getElement(CardElement);

    try {
      // Crea el pago en Stripe (esto es solo un ejemplo, deberías manejar la lógica real del pago en tu backend)
      const { error, paymentMethod } = await stripe.createPaymentMethod({
        type: "card",
        card: cardElement,
      });

      if (!error) {
        // Enviar los datos de pago al backend para crear el pago
        await axios.post("http://localhost:3000/api/pagos", {
          Reservacion_ID: reservationId,  // Usamos el ID de la reservación
          Monto_Total: total,
          Metodo_Pago: "card",
          Estado_Pago: "completado",
        });

        alert("Pago realizado con éxito.");
      } else {
        console.error(error);
        alert("Error al procesar el pago.");
      }
    } catch (error) {
      console.error("Error al realizar el pago:", error);
      alert("Ocurrió un error al procesar el pago.");
    }
  };

  return (
    <form onSubmit={handlePaymentSubmit}>
      <CardElement />
      <button type="submit" disabled={!stripe}>
        Pagar {total} €
      </button>
    </form>
  );
}
