import React, { useEffect, useState } from "react";
import axios from "axios";
import { Modal, Button } from "react-bootstrap";
import CommonHeading from "../common/CommonHeading";
import { useCart } from "../../pages/CartContext"; // Importamos el contexto del carrito
import Cart from "../Cart";  // Importamos el componente del carrito
const { addRoomToCart } = useCart();  // Aseg칰rate de que useCart est칠 correctamente importado y funcione.

import "./Rooms.css";

export default function Rooms() {
  const [rooms, setRooms] = useState([]);
  const [selectedRoom, setSelectedRoom] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const { addRoomToCart } = useCart(); // Usamos la funci칩n addRoomToCart desde el contexto

  useEffect(() => {
    axios
      .get("http://localhost:3000/api/habitaciones")
      .then((response) => {
        setRooms(response.data);
      })
      .catch((error) => {
        console.error("Error fetching room data:", error);
      });
  }, []);

  const handleDetailsClick = (room) => {
    setSelectedRoom(room);
    setShowModal(true);
  };

  const handleReserveClick = (room) => {
    addRoomToCart(room); // A침adimos la habitaci칩n seleccionada al carrito
  };

  const getImageUrl = (imagePath) => {
    if (imagePath && typeof imagePath === "string" && imagePath.length > 0) {
      return `http://localhost:3000${imagePath}`;
    }
    return "https://via.placeholder.com/300x200";
  };

  const getRoomClass = (estado) => {
    return estado === "Disponible" ? "room-item available" : "room-item not-available";
  };

  return (
    <div className="container-xxl py-5">
      <div className="container-fluid">
        <div className="row">
          {/* Main Rooms Section */}
          <div className="col-lg-9">
            <CommonHeading
              heading="Nuestras Habitaciones"
              title="Habitaciones"
              subtitle="Explora nuestras"
            />
            <div className="row g-4">
              {rooms.map((room, key) => (
                <div className="col-lg-4 col-md-6 wow fadeInUp" data-wow-delay="0.1s" key={key}>
                  <div className={getRoomClass(room.Estado) + " shadow rounded overflow-hidden"}>
                    <div className="position-relative">
                      <img
                        className="img-fluid room-img"
                        src={getImageUrl(room.Imagenes)}
                        alt={room.Nombre_Habitacion}
                      />
                      <small className="position-absolute start-0 top-100 translate-middle-y bg-primary text-white rounded py-1 px-3 ms-4">
                        ${room.Precio_Base || "N/A"}
                      </small>
                    </div>
                    <div className="p-4 mt-2">
                      <div className="d-flex justify-content-between mb-3">
                        <h5 className="mb-0">{room.Nombre_Habitacion}</h5>
                        <div className="ps-2">{room.Estado === "Disponible" ? "游릭" : "游댮"}</div>
                      </div>
                      <div className="d-flex mb-3">
                        <small className="border-end me-3 pe-3">
                          Capacidad: {room.Capacidad_Maxima} Mascotas
                        </small>
                        <small className="border-end me-3 pe-3">
                          Camas: {room.Cantidad_Camas || "N/A"}
                        </small>
                        <small>Ba침os Privados: {room.Banos_Propios ? "S칤" : "No"}</small>
                      </div>
                      <p className="text-body mb-3">{room.Descripcion || "Descripci칩n no disponible"}</p>
                      <div className="d-flex justify-content-between">
                        <button
                          className="btn btn-sm btn-primary rounded py-2 px-4"
                          onClick={() => handleDetailsClick(room)}
                        >
                          Ver Detalles
                        </button>
                        <button
                          className="btn btn-sm btn-dark rounded py-2 px-4"
                          onClick={() => handleReserveClick(room)}
                        >
                          Reservar Ahora
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {selectedRoom && (
              <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
                <Modal.Header closeButton>
                  <Modal.Title>{selectedRoom.Nombre_Habitacion}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                  <img
                    className="img-fluid mb-4"
                    src={getImageUrl(selectedRoom.Imagenes)}
                    alt={selectedRoom.Nombre_Habitacion}
                  />
                  <h5>Descripci칩n</h5>
                  <p>{selectedRoom.Descripcion}</p>
                  <h5>Detalles</h5>
                  <ul>
                    <li>Estado: {selectedRoom.Estado}</li>
                    <li>Capacidad: {selectedRoom.Capacidad_Maxima} mascotas</li>
                    <li>Tipo de habitaci칩n: {selectedRoom.Tipo_Habitacion}</li>
                    <li>Ubicaci칩n: {selectedRoom.Ubicacion}</li>
                    <li>Precio: ${selectedRoom.Precio_Base}</li>
                    <li>Ba침os Privados: {selectedRoom.Banos_Propios ? "S칤" : "No"}</li>
                    <li>Cantidad de camas: {selectedRoom.Cantidad_Camas || "N/A"}</li>
                  </ul>
                </Modal.Body>
                <Modal.Footer>
                  <Button variant="secondary" onClick={() => setShowModal(false)}>
                    Cerrar
                  </Button>
                </Modal.Footer>
              </Modal>
            )}
          </div>

          {/* Cart Section on the Right */}
          <div className="col-lg-3">
            <Cart />
          </div>
        </div>
      </div>
    </div>
  );
}
