import React, { createContext, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {jwtDecode} from "jwt-decode";
import axios from "axios";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userData, setUserData] = useState(null);  // Almacena los datos del usuario
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    console.log("Token en localStorage: ", token);  // Verificar el token en localStorage
    if (token) {
      const decodedToken = jwtDecode(token);
      const currentTime = Date.now() / 1000;
  
      if (decodedToken.exp > currentTime) {
        setIsAuthenticated(true);
        console.log("Token válido, autenticación establecida.");  // Verificar que el token es válido
        obtenerDatosUsuario(token);  // Obtener los datos del usuario
      } else {
        console.log("Token expirado, redirigiendo al login.");
        localStorage.removeItem("token");
        setIsAuthenticated(false);
        setUserData(null);
        navigate("/login");
      }
    } else {
      console.log("No se encontró token en localStorage, redirigiendo al login.");
    }
  }, [navigate]);

  
  const obtenerDatosUsuario = async (token) => {
    try {
      const response = await axios.get("http://localhost:3000/api/me", {
        headers: { "x-access-token": token },  // Enviando el token en el header
      });
      setUserData(response.data);  // Guardar los datos del usuario en el contexto
    } catch (error) {
      console.error("Error al obtener los datos del usuario:", error);
      localStorage.removeItem("token");
      setIsAuthenticated(false);
      setUserData(null);
      navigate("/login");
    }
  };
  
  const logout = () => {
    localStorage.removeItem("token");
    setIsAuthenticated(false);
    setUserData(null);
    navigate("/login");
  };

  return (
    <AuthContext.Provider value={{ isAuthenticated, setIsAuthenticated, userData, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => React.useContext(AuthContext);
