import React, { useState } from "react";
import Heading from "../components/common/Heading";
import axios from "axios"; // Importamos Axios
import "./Booking.css"; // Archivo CSS personalizado para la página de reservas

export default function Booking() {
  // Estado para manejar los datos del formulario
  const [formData, setFormData] = useState({
    nombreMascota: "",
    tipoHabitacion: "",
    fechaEntrada: "",
    fechaSalida: "",
    notas: "",
  });

  // Estado para manejar el mensaje de éxito o error
  const [message, setMessage] = useState("");

  // Función para manejar el cambio en los campos del formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Función para manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Realizamos la solicitud POST a la API
      const response = await axios.post("http://localhost:3000/api/reservaciones", {
        nombreMascota: formData.nombreMascota,
        tipoHabitacion: formData.tipoHabitacion,
        fechaEntrada: formData.fechaEntrada,
        fechaSalida: formData.fechaSalida,
        notas: formData.notas,
      });

      // Si la solicitud es exitosa, mostramos un mensaje de éxito
      setMessage("Reserva realizada con éxito.");
      console.log("Respuesta de la API:", response.data);

      // Limpiamos el formulario después del envío
      setFormData({
        nombreMascota: "",
        tipoHabitacion: "",
        fechaEntrada: "",
        fechaSalida: "",
        notas: "",
      });
    } catch (error) {
      // Si ocurre un error, mostramos un mensaje de error
      setMessage("Ocurrió un error al realizar la reserva. Inténtalo de nuevo.");
      console.error("Error al realizar la reserva:", error);
    }
  };

  return (
    <>
      <Heading heading="Reservas" title="Home" subtitle="Reservas" />
      <div className="booking-container">
        <form className="booking-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="nombreMascota">Nombre de la Mascota:</label>
            <input
              type="text"
              id="nombreMascota"
              name="nombreMascota"
              value={formData.nombreMascota}
              onChange={handleChange}
              required
              placeholder="Introduce el nombre de tu mascota"
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label htmlFor="tipoHabitacion">Tipo de Habitación:</label>
            <select
              id="tipoHabitacion"
              name="tipoHabitacion"
              value={formData.tipoHabitacion}
              onChange={handleChange}
              required
              className="form-control"
            >
              <option value="">Selecciona una opción</option>
              <option value="estándar">Estándar</option>
              <option value="deluxe">Deluxe</option>
              <option value="suite">Suite</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="fechaEntrada">Fecha de Entrada:</label>
            <input
              type="date"
              id="fechaEntrada"
              name="fechaEntrada"
              value={formData.fechaEntrada}
              onChange={handleChange}
              required
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label htmlFor="fechaSalida">Fecha de Salida:</label>
            <input
              type="date"
              id="fechaSalida"
              name="fechaSalida"
              value={formData.fechaSalida}
              onChange={handleChange}
              required
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label htmlFor="notas">Notas Adicionales:</label>
            <textarea
              id="notas"
              name="notas"
              value={formData.notas}
              onChange={handleChange}
              placeholder="Escribe alguna instrucción especial"
              className="form-control"
            ></textarea>
          </div>
          <button type="submit" className="btn btn-primary">Reservar</button>
        </form>
        {/* Mostramos el mensaje de éxito o error */}
        {message && <p className="message">{message}</p>}
      </div>
    </>
  );
}
