import React, { useState, useEffect } from "react";
import Heading from "../components/common/Heading";  // Usa tu componente de Heading
import axios from "axios";
import "./ReservasActivas.css";  // Archivo CSS para el diseño personalizado

export default function ReservasActivas() {
  const [reservas, setReservas] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    // Función para obtener las reservas activas
    const obtenerReservasActivas = async () => {
      try {
        const response = await axios.get("http://localhost:3000/api/reservaciones/activas");
        setReservas(response.data);
      } catch (error) {
        setError("Error al obtener las reservas activas.");
        console.error(error);
      }
    };

    obtenerReservasActivas();
  }, []);

  return (
    <>
      <Heading heading="Reservas Activas" title="Home" subtitle="Reservas Activas" />
      <div className="reservas-activas-container">
        <h1>Reservas Activas</h1>
        {error && <p className="error-message">{error}</p>}
        {reservas.length === 0 ? (
          <p>No hay reservas activas en este momento.</p>
        ) : (
          <table className="table">
            <thead>
              <tr>
                <th>ID Reservación</th>
                <th>ID Mascota</th>
                <th>ID Habitación</th>
                <th>Fecha Entrada</th>
                <th>Fecha Salida</th>
                <th>Estado</th>
                <th>Total</th>
                <th>Notas</th>
              </tr>
            </thead>
            <tbody>
              {reservas.map((reserva) => (
                <tr key={reserva.Reservacion_ID}>
                  <td>{reserva.Reservacion_ID}</td>
                  <td>{reserva.Mascota_ID}</td>
                  <td>{reserva.Habitacion_ID}</td>
                  <td>{new Date(reserva.Fecha_Entrada).toLocaleString()}</td>
                  <td>{new Date(reserva.Fecha_Salida).toLocaleString()}</td>
                  <td>{reserva.Estado_Reservacion}</td>
                  <td>${reserva.Total}</td>
                  <td>{reserva.Notas}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </>
  );
}
