import React, { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "../AuthContext";
import "./UserForm.css";  // Asegúrate de tener este archivo CSS para los estilos.

export default function UserForm() {
  const { userData } = useAuth();  // Datos del usuario desde el contexto
  const [userFormData, setUserFormData] = useState({
    nombreUsuario: "",
    email: "",
    avatarUrl: "",
  });
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  useEffect(() => {
    if (userData) {
      setUserFormData({
        nombreUsuario: userData.NOMBRE_USUARIO,
        email: userData.CORREO,
        avatarUrl: userData.AVATAR_URL || "",  // Si hay URL de avatar, la usamos
      });
    }
  }, [userData]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserFormData({
      ...userFormData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put("http://localhost:3000/api/usuarios/update", userFormData);
      setSuccessMessage("Perfil actualizado correctamente.");
    } catch (error) {
      setErrorMessage("Error al actualizar el perfil. Intenta nuevamente.");
      console.error("Error al actualizar el perfil:", error);
    }
  };

  return (
    <div className="profile-container">
      <h2 className="profile-title">Mi Perfil</h2>
      <div className="profile-avatar">
        <img src={userFormData.avatarUrl || "https://via.placeholder.com/150"} alt="User Avatar" />
        <p>{userFormData.email}</p>
      </div>
      <form onSubmit={handleSubmit} className="profile-form">
        <div className="form-group">
          <label htmlFor="nombreUsuario">Nombre *</label>
          <input
            type="text"
            id="nombreUsuario"
            name="nombreUsuario"
            className="form-control"
            value={userFormData.nombreUsuario}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="avatarUrl">URL Avatar *</label>
          <input
            type="url"
            id="avatarUrl"
            name="avatarUrl"
            className="form-control"
            value={userFormData.avatarUrl}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email Address *</label>
          <input
            type="email"
            id="email"
            name="email"
            className="form-control"
            value={userFormData.email}
            disabled  // No permitimos cambiar el email
          />
        </div>

        <button type="submit" className="btn btn-primary mt-3">
          Guardar Perfil
        </button>
        {successMessage && <p className="text-success">{successMessage}</p>}
        {errorMessage && <p className="text-danger">{errorMessage}</p>}
      </form>
    </div>
  );
}
