import React, { useState } from "react";
import Heading from "../components/common/Heading";

export default function Booking() {
  // Estado para manejar los datos del formulario
  const [formData, setFormData] = useState({
    nombreMascota: "",
    tipoHabitacion: "",
    fechaEntrada: "",
    fechaSalida: "",
    notas: "",
  });

  // Función para manejar el cambio en los campos del formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Función para manejar el envío del formulario
  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí iría la lógica para enviar los datos a la API
    console.log("Datos de la reserva:", formData);
  };

  return (
    <>
      <Heading heading="Reservas" title="Home" subtitle="Reservas" />
      <div className="booking-form">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="nombreMascota">Nombre de la Mascota:</label>
            <input
              type="text"
              id="nombreMascota"
              name="nombreMascota"
              value={formData.nombreMascota}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="tipoHabitacion">Tipo de Habitación:</label>
            <select
              id="tipoHabitacion"
              name="tipoHabitacion"
              value={formData.tipoHabitacion}
              onChange={handleChange}
              required
            >
              <option value="">Selecciona una opción</option>
              <option value="estándar">Estándar</option>
              <option value="deluxe">Deluxe</option>
              <option value="suite">Suite</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="fechaEntrada">Fecha de Entrada:</label>
            <input
              type="date"
              id="fechaEntrada"
              name="fechaEntrada"
              value={formData.fechaEntrada}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="fechaSalida">Fecha de Salida:</label>
            <input
              type="date"
              id="fechaSalida"
              name="fechaSalida"
              value={formData.fechaSalida}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="notas">Notas Adicionales:</label>
            <textarea
              id="notas"
              name="notas"
              value={formData.notas}
              onChange={handleChange}
            ></textarea>
          </div>
          <button type="submit">Reservar</button>
        </form>
      </div>
    </>
  );
}
