import React, { useState, useEffect } from "react";
import axios from "axios";
import CommonHeading from "../common/CommonHeading";
import { useNavigate } from "react-router-dom";
import "./Rooms.css"; // CSS personalizado para las habitaciones

export default function Rooms() {
  const navigate = useNavigate();
  const [rooms, setRooms] = useState([]);
  const [errorMessage, setErrorMessage] = useState("");

  useEffect(() => {
    // Cargar habitaciones desde la API
    axios.get("http://localhost:3000/api/habitaciones")
      .then((response) => {
        setRooms(response.data);
      })
      .catch((error) => {
        setErrorMessage("Error al cargar habitaciones. Intenta nuevamente.");
        console.error("Error al cargar habitaciones:", error);
      });
  }, []);

  const handleDetailsClick = (id) => {
    navigate(`/room/${id}`);
  };

  const handleDeleteClick = async (id) => {
    const confirmDelete = window.confirm("¿Estás seguro de eliminar esta habitación?");
    if (confirmDelete) {
      try {
        await axios.delete(`http://localhost:3000/api/habitaciones/${id}`);
        setRooms(rooms.filter((room) => room.id !== id)); // Eliminar de la lista sin recargar la página
        alert("Habitación eliminada correctamente.");
      } catch (error) {
        console.error("Error al eliminar la habitación:", error);
        setErrorMessage("Error al eliminar la habitación.");
      }
    }
  };

  return (
    <div className="container-xxl py-5">
      <div className="container">
        <CommonHeading
          heading="Nuestras Habitaciones"
          title="Habitaciones"
          subtitle="Explora nuestras"
        />
        {errorMessage && <p className="text-danger">{errorMessage}</p>}
        <div className="row g-4">
          {rooms.length > 0 ? (
            rooms.map((room) => (
              <div className="col-lg-4 col-md-6 wow fadeInUp" data-wow-delay="0.1s" key={room.id}>
                <div className="room-item shadow rounded overflow-hidden">
                  <div className="position-relative">
                    <img className="img-fluid" src={room.img || "/default-room.jpg"} alt="Habitación" />
                    <small className="position-absolute start-0 top-100 translate-middle-y bg-primary text-white rounded py-1 px-3 ms-4">
                      ${room.price} por noche
                    </small>
                  </div>
                  <div className="p-4 mt-2">
                    <div className="d-flex justify-content-between mb-3">
                      <h5 className="mb-0">{room.name}</h5>
                      <div className="ps-2">{room.star}</div>
                    </div>
                    <p className="text-body mb-3">{room.description}</p>
                    <div className="d-flex justify-content-between">
                      <button
                        className="btn btn-sm btn-primary rounded py-2 px-4"
                        onClick={() => handleDetailsClick(room.id)}
                      >
                        Ver Detalles
                      </button>
                      <button
                        className="btn btn-sm btn-danger rounded py-2 px-4"
                        onClick={() => handleDeleteClick(room.id)}
                      >
                        Eliminar
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <p>No hay habitaciones registradas.</p>
          )}
        </div>
      </div>
    </div>
  );
}
