import React from 'react';
import { useParams } from 'react-router-dom';
import { roomItems } from '../data/Data';  // Asegúrate de que la ruta esté bien

const RoomDetails = () => {
  const { id } = useParams();
  const room = roomItems.find(item => item.id === parseInt(id)); // Buscar habitación por ID

  if (!room) {
    return <h2>Habitación no encontrada</h2>;
  }

  return (
    <div className="container-xxl py-5">
      <div className="container">
        <h1>{room.name}</h1>
        <img src={room.img} alt={room.name} />
        <p>{room.description}</p>
        <h3>Precio: {room.price}</h3>
        <div className="d-flex mb-3">
          {room.facilities.map((facility, index) => (
            <small key={index} className="border-end me-3 pe-3">
              {facility.icon} {facility.quantity} {facility.name}
            </small>
          ))}
        </div>
      </div>
    </div>
  );
}

export default RoomDetails;
