import React, { useState, useEffect } from "react";
import axios from "axios";

export default function ReservasActivas() {
  const [reservas, setReservas] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    // Funci贸n para obtener las reservas activas
    const obtenerReservasActivas = async () => {
      try {
        const response = await axios.get("http://localhost:3000/api/reservaciones/activas");
        setReservas(response.data);  // Guardar las reservas en el estado
      } catch (error) {
        setError("Error al obtener las reservas activas.");
        console.error(error);
      }
    };

    obtenerReservasActivas(); // Llamada inicial
  }, []);

  return (
    <div className="reservas-activas-container">
      <h1>Reservas Activas</h1>
      {error && <p className="error-message">{error}</p>}
      {reservas.length === 0 ? (
        <p>No hay reservas activas en este momento.</p>
      ) : (
        <table className="table">
          <thead>
            <tr>
              <th>ID Reservaci贸n</th>
              <th>ID Mascota</th>
              <th>ID Habitaci贸n</th>
              <th>Fecha Entrada</th>
              <th>Fecha Salida</th>
              <th>Estado</th>
              <th>Total</th>
              <th>Notas</th>
            </tr>
          </thead>
          <tbody>
            {reservas.map((reserva) => (
              <tr key={reserva.Reservacion_ID}>
                <td>{reserva.Reservacion_ID}</td>
                <td>{reserva.Mascota_ID}</td>
                <td>{reserva.Habitacion_ID}</td>
                <td>{new Date(reserva.Fecha_Entrada).toLocaleString()}</td>
                <td>{new Date(reserva.Fecha_Salida).toLocaleString()}</td>
                <td>{reserva.Estado_Reservacion}</td>
                <td>${reserva.Total}</td>
                <td>{reserva.Notas}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
