import React, { createContext, useState, useContext } from 'react';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState({
    room: null,
    mascota: null,
    services: [] // Asegúrate de que 'services' es siempre un arreglo
  });

  // Función para agregar la habitación y la mascota al carrito
  const addRoomToCart = (room, mascota) => {
    setCartItems((prevItems) => ({
      ...prevItems,
      room,   // Actualizamos la habitación seleccionada
      mascota // Actualizamos la mascota seleccionada
    }));
  };

  // Función para agregar un servicio al carrito
  const addServiceToCart = (service) => {
    setCartItems((prevItems) => ({
      ...prevItems,
      services: [...prevItems.services, service] // Añadimos el nuevo servicio al arreglo de servicios
    }));
  };

  // Función para eliminar un servicio del carrito
  const removeServiceFromCart = (serviceID) => {
    setCartItems((prevItems) => ({
      ...prevItems,
      services: prevItems.services.filter(service => service.Servicio_ID !== serviceID) // Filtramos el servicio para eliminarlo
    }));
  };

  return (
    <CartContext.Provider value={{ cartItems, addRoomToCart, addServiceToCart, removeServiceFromCart }}>
      {children}
    </CartContext.Provider>
  );
};

// Hook para usar el contexto del carrito
export const useCart = () => useContext(CartContext);
