import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import CommonHeading from "../common/CommonHeading"; // Assuming you have a heading component

export default function Rooms() {
  const [rooms, setRooms] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch rooms data from the backend
    axios
      .get("http://localhost:3000/api/habitaciones") // Replace with your actual API route
      .then((response) => {
        setRooms(response.data);
      })
      .catch((error) => {
        console.error("Error fetching room data:", error);
      });
  }, []);

  const handleDetailsClick = (id) => {
    navigate(`/room/${id}`);
  };

  const getImageUrl = (imagePath) => {
    // Verificar si el campo de imagen est치 presente y no est치 vac칤o
    if (imagePath && typeof imagePath === "string" && imagePath.length > 0) {
      // Devolver la ruta completa si est치 disponible
      return `http://localhost:3000${imagePath}`; // Aseg칰rate de que esta es la URL correcta
    }
    return "https://via.placeholder.com/150"; // Devolver un placeholder si no hay imagen
  };

  return (
    <div className="container-xxl py-5">
      <div className="container">
        <CommonHeading
          heading="Nuestras Habitaciones"
          title="Habitaciones"
          subtitle="Explora nuestras"
        />
        <div className="row g-4">
          {rooms.map((room, key) => (
            <div className="col-lg-4 col-md-6 wow fadeInUp" data-wow-delay="0.1s" key={key}>
              <div className="room-item shadow rounded overflow-hidden">
                <div className="position-relative">
                  {/* Verificar si hay una URL de imagen v치lida */}
                  <img
                    className="img-fluid"
                    src={getImageUrl(room.Imagenes)} // Usar la funci칩n para obtener la URL correcta de la imagen
                    alt={room.Nombre_Habitacion}
                  />
                  <small className="position-absolute start-0 top-100 translate-middle-y bg-primary text-white rounded py-1 px-3 ms-4">
                    ${room.Precio_Base || 'N/A'} {/* Mostrar 'N/A' si no hay precio */}
                  </small>
                </div>
                <div className="p-4 mt-2">
                  <div className="d-flex justify-content-between mb-3">
                    <h5 className="mb-0">{room.Nombre_Habitacion}</h5>
                    <div className="ps-2">{room.Estado === 'Disponible' ? '游릭' : '游댮'}</div>
                  </div>
                  <div className="d-flex mb-3">
                    <small className="border-end me-3 pe-3">
                      Capacidad: {room.Capacidad_Maxima} personas
                    </small>
                    <small className="border-end me-3 pe-3">
                      Camas: {room.Cantidad_Camas || 'N/A'}
                    </small>
                    <small>
                      Ba침os Privados: {room.Banos_Propios ? 'S칤' : 'No'}
                    </small>
                  </div>
                  <p className="text-body mb-3">{room.Descripcion || 'Descripci칩n no disponible'}</p>
                  <div className="d-flex justify-content-between">
                    <button
                      className="btn btn-sm btn-primary rounded py-2 px-4"
                      onClick={() => handleDetailsClick(room.Habitacion_ID)}
                    >
                      Ver Detalles
                    </button>
                    <a className="btn btn-sm btn-dark rounded py-2 px-4" href="#">
                      Reservar Ahora
                    </a>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
