import React, { createContext, useState, useContext } from 'react';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState({
    room: null,
    mascota: null,
    services: []
  });

  // Función para agregar la habitación y la mascota al carrito
  const addRoomToCart = (room, mascota) => {
    setCartItems((prevItems) => ({
      ...prevItems,
      room,   // Actualizamos la habitación seleccionada
      mascota // Actualizamos la mascota seleccionada
    }));
  };

  // Función para agregar los servicios seleccionados al carrito
  const addServiceToCart = (services) => {
    setCartItems((prevItems) => ({
      ...prevItems,
      services  // Actualizamos los servicios seleccionados
    }));
  };

  return (
    <CartContext.Provider value={{ cartItems, addRoomToCart, addServiceToCart }}>
      {children}
    </CartContext.Provider>
  );
};

// Hook para usar el contexto del carrito
export const useCart = () => useContext(CartContext);
