// authcontext.js
import React, { createContext, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { jwtDecode} from "jwt-decode";

// Crear el contexto de autenticación
const AuthContext = createContext();

// Proveedor del contexto
export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userData, setUserData] = useState(null);  // Almacenar los datos del usuario
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      const decodedToken = jwtDecode(token);
      const currentTime = Date.now() / 1000; // Tiempo actual en segundos

      if (decodedToken.exp > currentTime) {
        setIsAuthenticated(true);
        setUserData(decodedToken);  // Decodifica y almacena la información del token
      } else {
        localStorage.removeItem("token");
        setIsAuthenticated(false);
        setUserData(null);  // Si expira, se limpia la información del usuario
        navigate("/login");
      }
    } else {
      setIsAuthenticated(false);
      setUserData(null);
    }
  }, [navigate]);

  const logout = () => {
    localStorage.removeItem("token");
    setIsAuthenticated(false);
    setUserData(null);  // Limpia la información del usuario al cerrar sesión
    navigate("/login");
  };

  return (
    <AuthContext.Provider value={{ isAuthenticated, setIsAuthenticated, userData, setUserData, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

// Hook para usar el contexto en otros componentes
export const useAuth = () => React.useContext(AuthContext);
