import { Link } from "react-router-dom";
import { footerContact, footerItem, socialIcons } from "../data/Data";

export default function Footer() {
  return (
    <>
      <div
        className="container-fluid bg-dark text-light footer wow fadeIn"
        data-wow-delay="0.1s"
      >
        <div className="container pb-3">  {/* Reducimos padding-bottom */}
          <div className="row g-3">  {/* Reducimos el espacio entre las filas */}
            {/* Sección de Información */}
            <div className="col-md-6 col-lg-4">
              <div className="bg-primary rounded p-2"> {/* Reducimos padding */}
                <Link to="/">
                  <h1 className="text-white text-uppercase mb-2" style={{ fontSize: "1.5rem" }}>PETHEAVEN</h1> {/* Reducimos tamaño del texto */}
                </Link>
                <p className="text-white mb-1" style={{ fontSize: "0.9rem" }}>
                  El lugar perfecto para tus mascotas
                </p>
              </div>
            </div>

            {/* Sección de Contacto */}
            <div className="col-md-6 col-lg-3">
              <h6 className="section-title text-start text-primary text-uppercase mb-3" style={{ fontSize: "1rem" }}>
                Contáctanos
              </h6>
              {footerContact.map((val, index) => (
                <p className="mb-1" key={index} style={{ fontSize: "0.9rem" }}>
                  {val.icon} {val.name}
                </p>
              ))}
              <div className="d-flex pt-1">
                {socialIcons.slice(0, 4).map((val, index) => (
                  <a className="btn btn-outline-light btn-social" href="" key={index}>
                    {val.icon}
                  </a>
                ))}
              </div>
            </div>

            {/* Sección de Enlaces Rápidos */}
            <div className="col-lg-5 col-md-12">
              <div className="row gy-2 g-2"> {/* Reducimos el espacio entre las columnas */}
                {footerItem.map((section, sectionIndex) => (
                  <div className="col-md-6" key={sectionIndex}>
                    <h6 className="section-title text-start text-primary text-uppercase mb-2" style={{ fontSize: "1rem" }}>
                      {section.header}
                    </h6>
                    {section.UnitItem.map((item, itemIndex) => (
                      <a className="btn btn-link" href="" key={itemIndex} style={{ fontSize: "0.9rem" }}>
                        {item.name}
                      </a>
                    ))}
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Sección inferior */}
      <div className="container-fluid bg-dark">
        <div className="container py-1">  {/* Reducimos padding-top y padding-bottom */}
          <p className="text-center text-white mb-0" style={{ fontSize: "0.8rem" }}>
            © 2024 PetHeaven. Todos los derechos reservados.
          </p>
        </div>
      </div>
    </>
  );
}
