import React, { useState } from "react";
import Heading from "../components/common/Heading";
import axios from "axios";
import "./Booking.css";

export default function Booking() {
  // Estado para manejar los datos del formulario
  const [formData, setFormData] = useState({
    mascotaId: "",
    habitacionId: "",
    fechaEntrada: "",
    fechaSalida: "",
    estadoReservacion: "Pendiente", // Valor por defecto
    total: "",
    notas: "",
  });

  // Estado para manejar el mensaje de éxito o error
  const [message, setMessage] = useState(""); // Definir el mensaje aquí

  // Función para manejar el cambio en los campos del formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Función para manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Formulario enviado, datos:", formData);

    try {
      const response = await axios.post("http://localhost:3000/api/reservaciones", {
        Mascota_ID: formData.mascotaId,
        Habitacion_ID: formData.habitacionId,
        Fecha_Entrada: formData.fechaEntrada,
        Fecha_Salida: formData.fechaSalida,
        Estado_Reservacion: formData.estadoReservacion,
        Total: formData.total,
        Notas: formData.notas,
      });

      console.log("Respuesta de la API:", response.data);
      setMessage("Reserva realizada con éxito.");

      setFormData({
        mascotaId: "",
        habitacionId: "",
        fechaEntrada: "",
        fechaSalida: "",
        estadoReservacion: "Pendiente",
        total: "",
        notas: "",
      });
    } catch (error) {
      setMessage("Ocurrió un error al realizar la reserva. Inténtalo de nuevo.");
      console.error("Error al realizar la reserva:", error);
    }
  };

  return (
    <>
      <Heading heading="Reservas" title="Home" subtitle="Reservas" />
      <div className="booking-container">
        <form className="booking-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="mascotaId">ID de la Mascota:</label>
            <input
              type="number"
              id="mascotaId"
              name="mascotaId"
              value={formData.mascotaId}
              onChange={handleChange} // Asegúrate de llamar a handleChange
              required
              placeholder="Introduce el ID de la mascota"
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label htmlFor="habitacionId">ID de la Habitación:</label>
            <input
              type="number"
              id="habitacionId"
              name="habitacionId"
              value={formData.habitacionId}
              onChange={handleChange} // Asegúrate de llamar a handleChange
              required
              placeholder="Introduce el ID de la habitación"
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label htmlFor="fechaEntrada">Fecha de Entrada:</label>
            <input
              type="datetime-local"
              id="fechaEntrada"
              name="fechaEntrada"
              value={formData.fechaEntrada}
              onChange={handleChange} // Asegúrate de llamar a handleChange
              required
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label htmlFor="fechaSalida">Fecha de Salida:</label>
            <input
              type="datetime-local"
              id="fechaSalida"
              name="fechaSalida"
              value={formData.fechaSalida}
              onChange={handleChange} // Asegúrate de llamar a handleChange
              required
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label htmlFor="total">Total de la Reserva:</label>
            <input
              type="number"
              step="0.01"
              id="total"
              name="total"
              value={formData.total}
              onChange={handleChange} // Asegúrate de llamar a handleChange
              required
              placeholder="Introduce el total en USD"
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label htmlFor="notas">Notas Adicionales:</label>
            <textarea
              id="notas"
              name="notas"
              value={formData.notas}
              onChange={handleChange} // Asegúrate de llamar a handleChange
              placeholder="Escribe alguna instrucción especial"
              className="form-control"
            ></textarea>
          </div>
          <button type="submit" className="btn btn-primary">Reservar</button>
        </form>
        {/* Mostramos el mensaje de éxito o error */}
        {message && <p className="message">{message}</p>}
      </div>
    </>
  );
}
